@use '../0-abstracts/variables' as *;
@use '../0-abstracts/mixins' as *;

html {
  scroll-behavior: smooth;
}
body {
  background: $clr-grey-1;
}
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
* {
  img {
    width: 100%;
    display: block;
  }
  h1,
  h2,
  h3,
  h4 {
    font-size: 2rem;
    text-transform: capitalize;
  }
  h4 {
    font-size: 1.8rem;
  }
  li {
    list-style: none;
  }
  a {
    text-decoration: none;
    color: inherit;
  }
}

// sidebar
.sidebar {
  min-height: 100vh;
  position: fixed;
  max-width: 600px;
  transform: translateY(-100%);
  inset: 0;
  border-radius: 50%;
  transition: $transition;
  z-index: 999;
  width: 100%;
  padding: 1rem;
  .logo {
    position: absolute;
    z-index: 1000;
    top: 1rem;
    left: 0;
    font-size: 2rem;
    width: 80%;
  }
  .sidebar-btns {
    position: absolute;
    height: -webkit-fit-content;
    height: -moz-fit-content;
    height: fit-content;
    top: 15%;
    left: 0;
    z-index: 1001;
  }
}
.sidebar::after {
  content: "";
  position: absolute;
  top: 0;
  overflow: hidden;
  left: 0;
  max-width: $fixed-width;
  width: 100%;
  height: 80%;
  @include circle();
  inset: 0;
  background: linear-gradient(45deg, $clr-primary-1 30%, $clr-primary-5);
  -webkit-filter: blur(5px);
  filter: blur(5px);
  z-index: 997;
}
.show-sidebar {
  transform: translate(0);
}
// end of sidebar
// section center
.section-center {
  padding: 1rem;
  min-height: 100vh;
  display: grid;
  z-index: 3;
  place-items: center;
  background: linear-gradient(45deg, rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.3)),
    url("../../img/pexels-leah-kelley-6480701.jpg") center/cover no-repeat;
  background-attachment: fixed;
  .sec-center {
    ul {
      max-width: $max-width;
      margin: 0 auto;
      display: grid;
      li {
        margin: 1rem auto;
      }
    }
  }
}
// end of section center
// about
#about {
  min-height: 100vh;
  overflow-y: hidden;
  background: linear-gradient(
    45deg,
    $primary-aqua-2 20%,
    rgba($primary-aqua-5, 0.1)
  );
  display: grid;
  place-content: center;
  @include rhombus;

  .about-info {
    width: 80%;
    border-radius: $border-radius;
    margin: 0 auto;
    padding: 1rem;
    position: relative;
    box-shadow: $light-shadow;
    z-index: 1;
    .header {
      width: 60%;
      margin: 0 auto;
      margin-bottom: 1rem;
    }
  }
  .about-info::before {
    content: "";
    position: absolute;
    width: 100%;
    border-radius: $border-radius;
    height: 100%;
    margin: 0 auto;
    left: 50%;
    transform: translateX(-50%);
    z-index: -1;
    background: transparent;
    -webkit-filter: blur(5px) contrast(150%);
    filter: blur(5px) contrast(150%);
    opacity: 0.4;
  }
}
// end of about
// products
.products-center {
  min-height: 100vh;
  background: linear-gradient(180deg, $clr-grey-1 50%, $clr-grey-8 60%);
  .header {
    width: 60%;
    margin: 0 auto;
    margin-bottom: 1rem;
  }
  .article {
    margin-top: 1rem;
    margin-bottom: 1rem;
    padding: 1rem;
    margin: 0 auto;
    width: 95%;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 1rem;
    .article-added {
      margin: 0 auto;
      margin-bottom: 2rem;
      border-radius: $border-radius;
      position: relative;
      z-index: 1;
      background: rgba($clr-grey-6, 1);
      box-shadow: $dark-shadow;
      max-width: 400px;
      .img-container {
        height: 13rem;
        width: 15rem;
        position: relative;
        border-radius: $border-radius;
        margin: 2rem auto;
        margin-bottom: 3rem;
        img {
          width: 100%;
          height: 100%;
          border-radius: inherit;
          display: block;
          -o-object-fit: cover;
          object-fit: cover;
        }
        .fa-home-img-product {
          position: absolute;
          left: 50%;
          transform: translate(-50%, -50%);
          font-size: 2rem;
          display: block;
          color: $primary-aqua-3;
          background: $clr-grey-6;
          padding: 0.5rem;
          border-radius: 50%;
        }
      }
      .img-container::before {
        content: "";
        position: absolute;
        width: 100%;
        height: 100%;
        top: -0.75rem;
        left: 0.75rem;
        z-index: -1;
        border: solid 0.2rem $primary-aqua-3;
      }
      .article-info {
        p {
          @include article-text();
          padding: 0.5rem;
          span a {
            @include article-text-a-link();
          }
        }
      }
    }
    .article-added::after {
      content: "";
      position: absolute;
      inset: -1rem;
      left: 50%;
      z-index: -1;
      top: 50%;
      background: rgba($clr-grey-6, 0.9);
      -webkit-filter: blur(5px);
      filter: blur(10px);
      width: 100%;
      height: 100%;
      transform: translate(-50%, -50%);
    }
  }
}
// end of products
// footer
.footer {
  display: grid;
  background: linear-gradient(180deg, rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.3)) center/cover no-repeat;
  padding: 0.5rem;
  // position: fixed;
  z-index: 1000;
  align-items: center;
  // bottom: 0;
  width: 100%;
  -webkit-animation: SlideInBottom 1.5s ease-out 1s 1 backwards;
  animation: SlideInBottom 1.5s ease-out 1s 1 backwards;
  .footer-text {
    justify-self: end;
    width: 100%;
    background: transparent;
    margin: 0 auto;
    max-width: $max-width;
    margin: 0 auto;
    align-items: center;
    background: transparent;
    -webkit-animation: nav-load-div1 1500ms ease-out;
    animation: nav-load-div1 1500ms ease-out;
    .icons {
      display: grid;
      grid-template-columns: repeat(3, 1fr);
      text-align: center;
      width: 100%;
      border-radius: 50%;
      margin-bottom: 0.5rem;
      background: transparent;
      li {
        background: transparent;
        list-style: none;
        border-radius: 50%;
        display: inline-block;
        position: relative;
        a {
          & {
            color: white;
            height: 1rem;
            width: 1rem;
            background: transparent;
            border-radius: 50%;
            font-size: 1.2rem;
            transition: 0.5s all linear;
            border-radius: inherit;
            &:hover {
              color: $clr-primary-3;
            }
          }
        }
      }
    }
  }
}
// end of footer

